import { all ,call,put,takeLatest,takeEvery, fork,take,delay} from 'redux-saga/effects';
import { LOG_IN_REQUEST, SIGN_UP_REQUEST, LOG_IN_FAILURE, SIGN_UP_SUCCESS ,SIGN_UP_FAILURE } from '../Reducer/user';
import { LOG_IN_SUCCESS } from '../Reducer/user';
import axios from 'axios';

function loginAPI(loginData){
    return axios.post('http://localhost:3065/api/user/login',loginData);
}

function* login(action){
    try{
        yield call(loginAPI,action.data);
        yield delay(2000);
        yield put({
            type:LOG_IN_SUCCESS
        })
    }catch(e){
        console.log(e)
        yield put({
            type:LOG_IN_FAILURE
        })
    }
}

function* watchLogin (){
    yield takeEvery(LOG_IN_REQUEST,login);
}

function signUpAPI(signUpData){
    return axios.post('http://localhost:3065/api/user',signUpData);
}

function* signUp(action) {
    try {
      yield call(signUpAPI,action.data);
      yield delay(2000);
      yield put({ // put은 dispatch 동일
        type: SIGN_UP_SUCCESS,
      });
    } catch (e) { // loginAPI 실패
      console.log(action);
      yield put({
        type: SIGN_UP_FAILURE,
        error: e,
      });
    }
  }


function* watchSignUp(){
    yield takeEvery(SIGN_UP_REQUEST,signUp)
}


export default function* userSaga(){
    yield all([
        fork(watchLogin),
        fork(watchSignUp)
    ]);
}
