import React,{useCallback} from 'react';
import { Card,Avatar,Button } from 'antd';
import { useSelector,useDispatch } from 'react-redux'; 
import {logoutAction} from '../Reducer/user'; 

const UserProfile = () => {
    const {user} = useSelector(state=>state.user);
    const dispatch = useDispatch();
    const onLogout =useCallback((e)=>{
        e.preventDefault();
        dispatch(logoutAction);
    },[])
    
    return (
        <Card
            actions={[
                <div key="twit">구독<br />{user.Post.length}</div>,
                <div key="following">팔로잉<br />{user.Following.length}</div>,
                <div key="follwer">팔로워<br />{user.Follower.length}</div>,
            ]}
        >
            <Card.Meta
                avatar={<Avatar>{user.nickname[0]}</Avatar>}
                title={user.nickname} />
            <Button type="primary" onClick={onLogout} loading={false}>로그아웃</Button>
        </Card>
    );
}


export default UserProfile;
