//call 함수 동기적으로 호출 fork 함수 비동기적으로 호출 put 액션 dispatch take 해당 액션이 dispatch 되면 제너레이터 next를 실행 하게함
//all 여러 이펙트를 동시에 실항 할 수 있게 함
//사가 자체 적으로 next() 호출을 자동으로 해주는 제너레이터임
//제너레이터는 한번 사용하면 다시 사용 할 수 없기 때문에 무한 루프를 사용하여 반복 실행이 될 수 있도록 한다
import { all ,call,put,takeLatest, fork,take} from 'redux-saga/effects';
import { LOG_IN } from '../Reducer/user';
import { LOG_IN_SUCCESS,LOG_IN_FAIL } from '../Reducer/user';

function loginAPI(){

}

function* login(){
    try{
        yield call(loginAPI);
        yield put({
            type:LOG_IN_SUCCESS
        })
    }catch(e){
        console.log(e)
        yield put({
            type:LOG_IN_FAIL
        })
    }
}

function* watchLogin(){
    while(true){
        yield take(LOG_IN)
        yield put({
            type:LOG_IN_SUCCESS
        })
        console.log("LOG_IN_SUCCESS");
    
    }
}



export default function* userSaga(){
    yield all([
        watchLogin()
    ]);
}
