import React from 'react';
import Link from 'next/link';
import { Menu, Input, Button, Row, Col, Card, Avatar, Form } from 'antd';
import {useSelector} from 'react-redux';
import LoginForm from '../components/LoginForm';
import UserProfile from './UserProfile';


const AppLayout = ({ children }) => {
    console.log(children)
    const { isLoggedIn } = useSelector(state=>state.user);
    return (
        <>
            <div>
                <Menu mode="horizontal">
                    <Menu.Item key="home"><Link href="/"><a>노드 버드</a></Link></Menu.Item>
                    <Menu.Item key="profile"><Link href="/Profile"><a>프로필</a></Link></Menu.Item>
                    <Menu.Item key="mail">
                        <Input.Search enterButton="search" style={{ verticalAlign: "middle" }} />
                    </Menu.Item>
                </Menu>
                <Row gutter={8}>
                    <Col style={{padding:'20px'}} xs={24} md={6}>
                        {isLoggedIn ?<UserProfile/>
                        :<LoginForm/>}
                        
                    </Col>
                    <Col style={{padding:'20px'}} xs={24} md={12}>
                        {children}
                    </Col>
                    <Col style={{padding:'20px'}} xs={24} md={6}>
                            <Link href="https://www.naver.com"><a target="_blank">Made in React+Next+Redux</a></Link>
                    </Col>
                </Row>
            </div>
        </>
    );
}


export default AppLayout;
