import {put, all, fork,takeLatest,delay } from 'redux-saga/effects';
import { ADD_POST_REQUEST, ADD_POST_SUCCESS, ADD_POST_FAILURE } from '../Reducer/post';

function* appPost(){
  try{
    yield delay(2000);
    yield put({
      type:ADD_POST_SUCCESS,
    })
  }catch(e){
    yield put({
      type:ADD_POST_FAILURE,
      error:e
    })
  }
}

function* watchAddPost(){
  yield takeLatest(ADD_POST_REQUEST,appPost);
}

export default function* userSaga(){
    yield all([
      fork(watchAddPost),
    ]);
}
