import React, { useCallback, useState ,useEffect } from 'react';
import { Form, Input, Button, } from 'antd';
import {useSelector, useDispatch} from 'react-redux';
import { ADD_POST_REQUEST } from '../Reducer/post';

const PostForm = () => {
    const [ text , setText ] = useState('');
    const dispatch = useDispatch();
    const {imagePaths,isAddingPost,postAdded} = useSelector(state=>state.post)

    useEffect(()=>{
        setText('');
    },[postAdded])
    
    const onSubmitForm = useCallback((e) => {
        e.preventDefault();
        dispatch({
            type:ADD_POST_REQUEST,
            data:{
                content:text,
            }
        })
    },[])

    const onChangeText = useCallback((e) => {
        setText(e.target.value);
        console.log(text);
    },[text]);
    
    return (
        <Form encType="multipart/form-data" onSubmit = {onSubmitForm} >
            <Input.TextArea value={text} onChange={onChangeText}  maxLength={140} placeholder="어떤 신기한 일이 있었나여"></Input.TextArea>
            <div>
                <Input type="file" multiple hidden />
                <Button>이미지 업로드</Button>
                <Button type="primary" style={{ float: "right" }} htmlType="submit" loading={isAddingPost} >짹짹</Button>
                <div>
                    {imagePaths.map((val, idx) => {
                        return (
                            <div key={idx} style={{ display: 'inline-block' }} >
                                <img src={"http://localhost:3065/"} style={{ width: '200px' }} alt={idx} />
                                <div>
                                    <Button>제거</Button>
                                </div>
                            </div>
                        )
                    })}
                </div>
            </div>
        </Form>
    );
}

export default PostForm;
