//call 함수 동기적으로 호출 fork 함수 비동기적으로 호출 put 액션 dispatch take 해당 액션이 dispatch 되면 제너레이터 next를 실행 하게함
//사가 자체 적으로 next() 호출을 자동으로 해주는 제너레이터임
import { all ,call,put,takeLatest, fork } from 'redux-saga/effects';
import { LOG_IN } from '../Reducer/user';
import { LOG_IN_SUCCESS,LOG_IN_FAIL } from '../Reducer/user';

export const HELLO_SAGA = "HELLO_SAGA";

function loginAPI(){

}

function* login(){
    try{
        yield call(loginAPI);
        yield put({
            type:LOG_IN_SUCCESS
        })
    }catch(e){
        console.log(e)
        yield put({
            type:LOG_IN_FAIL
        })
    }
}

function* watchLogin(){
    yield takeLatest(LOG_IN,login)
}

function* helloSaga(){
    yield take(HELLO_SAGA)
    console.log("Hello Saga");
}

export default function* userSaga(){
    yield all([
        fork(watchLogin),
        helloSaga(),
    ]);
}
