import React from 'react';
import Head from 'next/head';
import PropTypes from 'prop-types';
import AppLayout from '../components/AppLayout';
import withRedux from 'next-redux-wrapper';
import { Provider } from 'react-redux';
import {createStore, compose, applyMiddleware} from 'redux';
import reducer from '../Reducer';
//모든 페이지에 공통적으로 들어가는것들을 여기다모음


const Nodebird = ({Component,store}) =>{
    return(
        <Provider store={store}>
            <Head>
                <title>NodeBird</title>
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.22.0/antd.css" />
            </Head>
            <AppLayout>
                <Component />
            </AppLayout>
        </Provider>
    );
}
//store 만드는 과정 withRedux라는 고위함수를 만들어 주는데 확장자 처럼 사용되며
//state+reducer가 합쳐진 것을 모으는 곳이 store라고함
// export default withRedux((initialState,options)=>{
//     const middlewares = [];
//     const enhancer =  compose(
//         applyMiddleware(...middlewares)
//         ,typeof(window) !== 'undefined' && window.__REDUX_DEVTOOLS_EXTENSION__ !== 'undefined' ? 
//         window.__REDUX_DEVTOOLS_EXTENSION__():(f)=>f);
//     const store=createStore(reducer,initialState,enhancer);
// //middlewares는 state,action,reducer와 store사이 기능을 추가 변조 할수있는 역할을 가짐    
//     return store;
// })(Nodebird);


const configureStore = (initialState, options) => {
    const sagaMiddleware = createSagaMiddleware();
    const middlewares = [sagaMiddleware];
    const enhancer = process.env.NODE_ENV === 'production'
      ? compose(applyMiddleware(...middlewares))
      : compose(
        applyMiddleware(...middlewares),
        !options.isServer && typeof window.__REDUX_DEVTOOLS_EXTENSION__ !== 'undefined' ? window.__REDUX_DEVTOOLS_EXTENSION__() : f => f,
      );
    const store = createStore(reducer, initialState, enhancer);
    sagaMiddleware.run(rootSaga);
    return store;
  };
  
  export default withRedux(configureStore)(NodeBird);
