export const initialState={
    mainPosts: [{
        User: {
            id: 1,
            nickname: 'dbals',
            content: '첫 번째 게시글',
            img: 'https://cdn.crowdpic.net/detail-thumb/thumb_d_FF1D6AD8B887EC2359A69C049ECA2A1D.jpg'
        },
        content: '첫 번째 게시글',
        img: 'https://cdn.crowdpic.net/detail-thumb/thumb_d_FF1D6AD8B887EC2359A69C049ECA2A1D.jpg'
    }],
    imagePaths: [],
};

export const ADD_POST='ADD_POST';
export const ADD_DUMMY='ADD_DUMMY';

const addPost ={
    type:ADD_POST,
}

const addDummy ={
    type:ADD_DUMMY,
    data:{
        content:'Hello',
        UseId:1,
        User:{
            nickname:'dbals',
        }
    }
}


const reducer =(state=initialState,action)=>{
    switch(action.type){
        case ADD_POST:{
            return{
                ...state,
            }
        }case ADD_DUMMY:{
            return{
                ...state,
                mainPost:[action.data,...state.mainPost]
            }
        }default:{
            return{
                ...state
            } 
        }
    }
}


export default reducer;
