import React from 'react';
import Link from 'next/link';

const PostCardCotent = ({ postData }) => {
  return (
    <div>
      {postData.split(/(#[^\s]+)/g).map(v => {
        if (v.match(/(#[^\s]+)/g)) {
          return (
            <Link
              href={{
                pathname: "/hashtag",
                query: { tag: v.slice(1) }
              }}
              as={`/hashtag/${v.slice(1)}`}
              key={v}
            >
              <a>{v}</a>
            </Link>
          );
        }
        return v;
      })}
    </div>
  );
};


export default PostCardCotent;
