import { all ,call,put,takeLatest,takeEvery, fork,take,delay} from 'redux-saga/effects';
import { LOG_IN, LOG_IN_REQUEST } from '../Reducer/user';
import { LOG_IN_SUCCESS,LOG_IN_FAIL } from '../Reducer/user';

function loginAPI(){

}

function* login(){
    try{
        yield call(loginAPI);
        yield put({
            type:LOG_IN_SUCCESS
        })
    }catch(e){
        console.log(e)
        yield put({
            type:LOG_IN_FAIL
        })
    }
}

function* watchLogin (){
    yield takeEvery(LOG_IN_REQUEST,login);
}


export default function* userSaga(){
    yield all([
        fork(watchLogin),
    ]);
}
