import React, { useCallback, useState } from 'react';
import { Form, Input, Button, } from 'antd';
import {useSelector, useDispatch} from 'react-redux';

const PostForm = () => {
    const [ text , setText ] = useState('');
    const dispatch = useDispatch();
    const {imagePaths,isAddingPost} = useSelector(state=>state.post)
    
    const onSubmitForm = useCallback(() => {

    },[])

    const onChangeText = useCallback((e) => {
        setText(e.target.value);
    },[text]);
    
    return (
        <Form encType="multipart/form-data" onSubmit = {onSubmitForm} >
            <Input.TextArea value={text} onChange={onChangeText}  maxLength={140} placeholder="어떤 신기한 일이 있었나여"></Input.TextArea>
            <div>
                <Input type="file" multiple hidden />
                <Button>이미지 업로드</Button>
                <Button type="primary" style={{ float: "right" }} htmlType="submit" isLoading={isAddingPost} >짹짹</Button>
                <div>
                    {imagePaths.map((val, idx) => {
                        return (
                            <div key={idx} style={{ display: 'inline-block' }} >
                                <img src={"http://localhost:3065/"} style={{ width: '200px' }} alt={idx} />
                                <div>
                                    <Button>제거</Button>
                                </div>
                            </div>
                        )
                    })}
                </div>
            </div>
        </Form>
    );
}

export default PostForm;
