import React, { useState } from "react";
import Slick from "react-slick";
import { Icon } from "antd";
import style from "styled-components";

const Overlay = style.div`
  position:fixed;
  z-index:5000;
  top:0;
  left:0;
  right:0;
  bottom:0;
`;

const Header = style.header`
  height : 44px;
  background:white;
  position:relative;
  padding:0;
  text-align:center;
  &h1{
    margin: 0;
    font-size: 30px;
    color: red;
    line-height: 44px;
  }
`;

const ImagesZoom = ({ images, onClose }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  return (
    <Overlay>
        <Header>
            <h1>상세 이미지</h1>
            <Icon
              type="close"
              onClick={onClose}
              style={{
                position: "absolute",
                right: 0,
                top: 0,
                padding: 15,
                lineHeight: "14px",
                cursor: "pointer"
              }}
            />
        </Header>
        <div style={{ height: "calc(100% - 44px)", background: "#090909" }}>
          <div>
            <Slick
              initialSlide={0}
              afterChange={slide => setCurrentSlide(slide)}
              infinite={false}
              arrows
              slidesToShow={1}
              slidesToScroll={1}
            >
              {images.map(v => {
                return (
                  <div style={{ padding: 32, textAlign: "center" }}>
                    <img
                      src={`http://localhost:3065/${v.src}`}
                      style={{ margin: "0 auto", maxHeight: 750 }}
                    />
                  </div>
                );
              })}
            </Slick>
            <div style={{ textAlign: "center" }}>
              <div
                style={{
                  width: 75,
                  height: 30,
                  lineHeight: "30px",
                  borderRadius: 15,
                  background: "#313131",
                  display: "inline-block",
                  textAlign: "center",
                  color: "white",
                  fontSize: "15px"
                }}
              >
                {currentSlide + 1} / {images.length}
              </div>
            </div>
          </div>
        </div>
    </Overlay>
  );
};

export default ImagesZoom;
