import React, { useState, useCallback, useEffect } from "react";
import Link from 'next/link';
import { Card, Icon, Button, Avatar, Divider, Form, Input, List,Comment } from "antd";
import { useSelector, useDispatch } from "react-redux";
import { ADD_POST_REQUEST, ADD_COMMENT_REQUEST } from "../Reducer/post";

const PostCard = ({ post }) => {
  const [commentFormOpened, setCommentOpened] = useState(false);
  const [commentText, setCommentText] = useState("");
  const { me } = useSelector(state => state.user);
  const { commentAdded ,isAddingComment } = useSelector(state => state.post);
  const dispatch = useDispatch();

  const onToggleComment = useCallback(() => {
    setCommentOpened(prev => !prev);
  }, []);

  const onSubmitComment = useCallback((e) =>{
      e.preventDefault();
      console.log(me)
      if(!me){
          return alert('로그인 필요합니다');
      }
      console.log(post)
      dispatch({
          type:ADD_COMMENT_REQUEST,
          data: {
              postId:post.id
          }
      })
  },[me && me.id])

  const onChangeCommentText = useCallback((e) =>{
      setCommentText(e.target.value)
  },[]);

  useEffect(()=>{
    setCommentText('');
  },[commentAdded === true])
  console.log(post.createdAt)
  return (
    <div>
      <Card
        key={+post.createdAt}
        cover={post.img && <img alt="example" src={post.img} />}
        actions={[
          <Icon type="retweet" key="retweet" />,
          <Icon type="heart" key="heart" />,
          <Icon type="message" key="message" onClick={onToggleComment} />,
          <Icon type="ellipsis" key="ellipsis" />
        ]}
        extra={<Button>팔로우</Button>}
      >
        <Card.Meta
          avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
          title={post.User.nickname}
      description={
      <div>
        {post.content.split(/(#[^\s]+)/g).map( v=>{
          if(v.match(/(#[^\s]+)/g)){
            return(
            <Link href={`/hashtag/${v.slice(1)}`} key={v} ><a>{v}</a></Link>
            )
          }
          return v;
        } )}
        </div>}
        />
      </Card>
      {commentFormOpened && 
      <>
        <Form onSubmit = {onSubmitComment} >
            <Form.Item>
                <Input.TextArea rows={4} value={commentText} onChange={onChangeCommentText} />
            </Form.Item>
            <Button type="primary" htmlType="submit" loading={isAddingComment} > 입력!! </Button>
        </Form>
        <List 
            header ={`${post.Comments ? post.Comments.length : 0 } 댓글`}
            itemLayout="horizontal"
            dataSource={post.Comments || [] }
            renderItem={item=>(<li>
                <Comment
                    author = {item.User.nickname}
                    avatar = {<Avatar>{item.User.nickname[0]}</Avatar>}
                    content = {item.content}
                    datetime = {item.createAt}
                />
            </li>)}
         />
      </>}
    </div>
  );
};

export default PostCard;
