import React, { useEffect } from 'react';
import { Form, Input, Button, Card, Icon, Avatar } from 'antd';
import PostForm from '../components/PostForm';
import PostCard from '../components/PostCard';
import { useDispatch, useSelector } from 'react-redux';
import { loginAction, logoutAction } from '../Reducer/user';

//useDispatch은 action을 실행을 담당하고 있고
//useSelector은 reducer에 있는 state를 가지고 올 수 있다


const Home = () => {
    const dispatch = useDispatch();
    const { isLoggedIn,user } = useSelector(state=>state.user);
    const { mainPosts } = useSelector(state=>state.post);
    useEffect(() => {
    }, [])
    return (
        <>
            {user?<div>{user.nickname}이 로그인 하셨습니다</div>:<div>로그아웃 하셨습니다</div>}
            <div>
                {isLoggedIn && <PostForm />}
                {mainPosts.map((val) => {
                    return (
                        <PostCard post={val} key={val} />
                    );
                })}
            </div>
        </>
    );
};

export default Home;
