import {put, all, fork,takeLatest,delay,call } from 'redux-saga/effects';
import { ADD_POST_REQUEST, ADD_POST_SUCCESS, ADD_POST_FAILURE ,ADD_COMMENT_FAILURE,ADD_COMMENT_REQUEST,ADD_COMMENT_SUCCESS, LOAD_MAIN_POSTS_SUCCESS, LOAD_MAIN_POSTS_REQUEST, LOAD_MAIN_POSTS_FAILURE, LOAD_HASHTAG_POSTS_REQUEST, LOAD_HASHTAG_POSTS_SUCCESS, LOAD_HASHTAG_POSTS_FAILURE, LOAD_USER_POSTS_REQUEST, LOAD_USER_POSTS_SUCCESS, LOAD_USER_POSTS_FAILURE } from '../Reducer/post';
import axios from 'axios';

function addPostAPI (postData) {
  return axios.post('http://localhost:3065/api/post',postData,{
    withCredentials:true,
  })
}

function* appPost(action){
  try{
    const result = yield call(addPostAPI,action.data)
    yield put({
      type:ADD_POST_SUCCESS,
      data:result.data,
    })
  }catch(e){
    yield put({
      type:ADD_POST_FAILURE,
      error:e
    })
  }
}

function* watchAddPost(){
  yield takeLatest(ADD_POST_REQUEST,appPost);
}
function loadMainPostsAPI () {
  return axios.get('http://localhost:3065/api/posts')

}

function* loadMainPosts(){
  try{
    const result = yield call(loadMainPostsAPI);
    yield put({
      type:LOAD_MAIN_POSTS_SUCCESS,
      data: result.data
    })
  }catch(e){
    yield put({
      type:LOAD_MAIN_POSTS_FAILURE,
      error:e
    })
  }
}

function* watchMainLoadPosts(){
  yield takeLatest(LOAD_MAIN_POSTS_REQUEST,loadMainPosts);
}
function loadUserPostsAPI (id) {
  return axios.get(`http://localhost:3065/api/user/${id}/posts`)

}

function* loadUserPosts(action){
  try{
    const result = yield call(loadUserPostsAPI,action.data);
    yield put({
      type:LOAD_USER_POSTS_SUCCESS,
      data: result.data
    })
  }catch(e){
    yield put({
      type:LOAD_USER_POSTS_FAILURE,
      error:e
    })
  }
}

function* watchLoadUserPosts(){
  yield takeLatest(LOAD_USER_POSTS_REQUEST,loadUserPosts);
}
function loadHashtagPostsAPI (tag) {
  return axios.get(`http://localhost:3065/api/hashtag/${tag}`,tag)

}

function* loadHashtagPosts(action){
  try{
    const result = yield call(loadHashtagPostsAPI,action.data);
    yield put({
      type:LOAD_HASHTAG_POSTS_SUCCESS,
      data: result.data
    })
  }catch(e){
    yield put({
      type:LOAD_HASHTAG_POSTS_FAILURE,
      error:e
    })
  }
}

function* watchLoadHashtagPosts(){
  yield takeLatest(LOAD_HASHTAG_POSTS_REQUEST,loadHashtagPosts);
}

function addCommentAPI () {

}

function* addComment(action){
  try{
    yield put({
      type:ADD_COMMENT_SUCCESS,
      data:{
        postId : action.data.postId
      }
    })
  }catch(e){
    yield put({
      type:ADD_COMMENT_FAILURE,
      error:e
    })
  }
}

function* watchAddComment(){
  yield takeLatest(ADD_COMMENT_REQUEST,addComment);
}

export default function* userSaga(){
    yield all([
      fork(watchAddPost),
      fork(watchAddComment),
      fork(watchMainLoadPosts),
      fork(watchLoadHashtagPosts),
      fork(watchLoadUserPosts),
    ]);
}
